mapper.insert:
INSERT INTO tbl_goods 
		(gdsName, cateCode, gdsPrice, gdsStock, gdsDes)
VALUES 	(#{gdsName}, #{cateCode}, #{gdsPrice}, #{gdsStock}, #{gdsDes});

mapper.update:
UPDATE 	tbl_goods
SET 	gdsName = #{gdsName}
	,	cateCode = #{cateCode}
	,	gdsPrice = #{gdsPrice}
	,	gdsStock = #{gdsStock}
	,	gdsDes = #{gdsDes}
WHERE 	gdsNum = #{gdsNum}
